from random import sample

def generate_text(grams_list, text_length=100):
	n = len(grams_list[0])
	initial = sample(grams_list, 1)[0]
	text_generated = [initial[i] for i in range(n)]
	for i in range(text_length-n):
		if n > 1:
			_list = []
			for item in grams_list:
				if initial[1:] == item[0:n-1]:
					_list.append(item)
			next_sample = sample(_list, 1)[0]
			text_generated.append(next_sample[n-1])
			initial = next_sample
		else:
			next_sample = sample(grams_list, 1)
			text_generated.append(next_sample[0][0])
	text_generated = "".join(text_generated)
	return text_generated

print(generate_text(eight_grams))